#include<iostream>
using namespace std;

class Node {
public:
    int data;
    Node* next;
    Node(int d) { data = d; next = nullptr; }
};

class LinkedList {
public:
    Node* head;
    LinkedList() { head = nullptr; }

    void push(int new_data) {
        Node* new_node = new Node(new_data);
        new_node->next = head;
        head = new_node;
    }
};

int main() {
    LinkedList list;
    list.push(3);
    list.push(2);
    list.push(1);
    return 0;
}

